import { IObject } from "soui4/IObject";
import { IInterpolator } from "soui4/IInterpolator";

export class IAnimation extends IObject{
    /**
     * clone animation
     */
    clone():IAnimation;

    /**
     * copy animation from src
     * @param src source animation
     */
    copy(src:IAnimation):void;

    /**
     * reset animation state
     */
    reset():void;

    /**
     * cancel current animation
     */
    cancel():void;

    /**
     * set interpolator object
     * @param ip interpolator object
     */
    setInterpolator(ip:IInterpolator):void;

    /**
     * get interpolator object
     */
    getInterpolator():IInterpolator;

    /**
     * set animation duration
     * @param duration duration in ms
     */
    setDuration(duration:number):void;

    /**
     * set animation start offset
     * @param ts start offset in ms
     */
    setStartOffset(ts:number):void;

    /**
     * start on the next frame
     */
    start():void;

    /**
     * start right now
     */
    startNow():void;

    /**
     * set repeat mode
     * @param mode repeat mode. can be soui4.RepeatMode_Restart,soui4.RepeatMode_Reverse
     */
    setRepeatMode(mode:number):void;

    /**
     * get repeat mode
     */
    getRepeatMode():number;

    /**
     * set repeat count
     * @param count repeat count
     */
    setRepeatCount(count:number):void;

    /**
     * get repeat count
     */
    getRepeatCount():number;

    /**
     * return was animation started
     */
    hasStarted():boolean;

    /**
     * return was animation ended
     */
    hasEnded():boolean;

    /**
     * return will animation change alpha
     */
    hasAlpha():boolean;

    /**
     * set user data
     * @param uData 
     */
    setUserData(uData:number):void;

    /**
     * get user data
     */
    getuserData():number;
}