import { IObject } from "soui4/IObject";
import { IObjRef } from "soui4/IObjRef";

export class IEvtArgs extends IObjRef{
    /**
     * 返回事件对象的Sender
     */
    Sender():IObject;

    /**
     * 返回事件对象Sender的ID
     */
    IdFrom():number;

    /**
     * 返回事件对象Sender的Name
     */
    NameFrom():string;

    /**
     * 返回事件是否需要继续冒泡
     */
    IsBubbleUp():boolean;

    /**
     * 设置事件继续冒泡标志
     * @param flag 冒泡标志
     */
    SetBubbleUp(flag:boolean):void;
}

export class IEvtSlot extends IObjRef{
    /**
     * 运行事件槽，返回事件是否被处理
     */
    Run():boolean;

    /**
     * clone一个事件槽对象
     */
    Clone():IEvtSlot;

    /**
     * 判断两个对象是否相同
     * @param src 比较的对象
     */
    Equal(src:IEvtSlot):boolean;

    /**
     * 获取类型
     */
    GetSlotType():number;
}