import { IObjRef } from "soui4/IObjRef";

export class IBitmapS extends IObjRef{
    /**
     * get image width
     */
    Width():number;

    /**
     * get image height
     */
    Height():number;

    /**
     * clone a new image object
     */
    Clone():IBitmapS;

    /**
     * save image to file
     * @param path dest file name
     * @param fmt image format, only gdip_decoder is avaiable
     */
    Save(path:string):number;
}