import { IObjRef } from "soui4/IObjRef";
import { IBitmapS } from "soui4/IImage";

export class IResProvider extends IObjRef{
    /**
     * 初始化资源包
     * @param wp 参数1
     * @param lp 参数2
     */
    Init(wp:number,lp:number):boolean;

    /**
     * 判断资源是否存在
     * @param type 资源类型
     * @param name 资源名
     */
    HasResource(type:string,name:string):boolean;

    /**
     * 加载图标资源
     * @param name 图标资源名
     * @param cx 图标宽度
     * @param cy 图标高度
     */
    LoadIcon(name:string,cx:number,cy:number):number;

    /**
     * 加载图片
     * @param type 资源类型
     * @param name 资源名
     */
    LoadImage(type:string,name:string):IBitmapS;

    /**
     * 获取资源文件长度
     * @param type 资源类型
     * @param name 资源名
     */
    GetRawBufferSize(type:string,name:string):number;    
}