import { IBitmapS } from "soui4/IImage";
import { IObjRef } from "soui4/IObjRef";
import { IResProvider } from "soui4/IResProvider";

export class IResProviderMgr extends IObjRef{
    /**
     * 增加资源包
     * @param resprovider 资源包
     * @param uidef uidef文件资源ID
     */
    AddResProvider(resprovider:IResProvider,uidef:string):void;

    /**
     * 移除指定资源包
     * @param resprovider 资源包
     */
    RemoveResProvider(resprovider:IResProvider):void;

    /**
     * 删除所有资源包
     */
    RemoveAll():void;

    /**
     * 获取第一个资源包
     */
    GetHeadResProvider():IResProvider;

    /**
     * 获取最后一个资源包
     */
    GetTailResProvider():IResProvider;

    /**
     * 设置使用文件资源时，文件所在的路径起始位置
     * @param prefix 文件所在的路径起始位置
     */
    SetFilePrefix(prefix:string):void;

    /**
     * 判断资源是否存在
     * @param type 资源类型
     * @param name 资源名
     */
    HasResource(type:string,name:string):boolean;

    /**
     * 加载图片
     * @param type 资源类型
     * @param name 资源名
     */
    LoadImage(type:string,name:string):IBitmapS;

    /**
     * 获取资源文件长度
     * @param type 资源类型
     * @param name 资源名
     */
    GetRawBufferSize(type:string,name:string):number;
}