import { IApplication } from "soui4/IApplication";
import { IEvtSlot } from "soui4/IEvt";
import { IHostWnd } from "soui4/IHostWnd";
import { IMenu } from "soui4/IMenu";
import { IMenuEx } from "soui4/IMenuEx";
import { INativeWnd } from "soui4/INativeWnd";
import { IObjRef } from "soui4/IObjRef";
import { IResProvider } from "soui4/IResProvider";
import { IStringA } from "soui4/IString";
import { ITimer } from "soui4/ITimer";
import { IXmlDoc } from "soui4/IXml";

export class ISouiFac extends IObjRef{
	/** CreateApp
	 * @brief 创建IApp对象
	 * @return IApplication * -- App对象
	 */
	CreateApp( pRenderFac:IRenderFactory,hInst:HMODULE ,pszHostClassName:string, bImeApp:boolean):IApplication;

	/** CreateNativeWnd
	 * @brief 创建窗口对象
	 * @return INativeWnd * -- 窗口对象
	 */
	CreateNativeWnd():INativeWnd;

	/** CreateHostWnd
	 * @brief 创建SOUI窗口对象
	 * @return IHostWnd * -- SOUI窗口对象
	 */
	CreateHostWnd( pszResID:string):IHostWnd;


	/** CreateHostDialog
	 * @brief 创建SOUI对话框对象
	 * @return IHostDialog * -- SOUI对话框对象
	 */
	 CreateHostDialog( pszResID:string) :IHostDialog;

	/** CreateStringA
	 * @brief 创建IStringA对象
	 * @return IStringA * -- 字符串对象
	 */
	CreateStringA( pszSrc:string ):IStringA;

	/** CreateStringW
	 * @brief 创建IStringW对象
	 * @return IStringW * -- 字符串对象
	 */
	CreateStringW( pszSrc:string) :IStringW;

	/** CreateXmlDoc
	 * @brief 创建IXmlDoc对象
	 * @return IXmlDoc * -- Xml对象
	 */
	CreateXmlDoc() :IXmlDoc;

	/** CreateResProvider
	 * @brief 创建IResProvider对象
	 * @return IResProvider * -- 资源包对象
	 */
	CreateResProvider( resType:BUILTIN_RESTYPE ) :IResProvider;

	/** CreateMenu
	 * @brief 创建Menu对象
	 * @return IMenu * -- Menu对象
	 */
	CreateMenu(  hMenu:HMENU):IMenu;

	/** CreateMenuEx
	 * @brief 创建MenuEx对象
	 * @return IMenuEx * -- MenuEx对象
	 */
	CreateMenuEx() :IMenuEx;

	/** CreateTimer
	 * @brief 创建定时器对象
	 * @return ITimer * -- 定时器对象
	 */
	CreateTimer( pEvtSlot:IEvtSlot) :ITimer;    
}