export class IStringA{
    GetLength():number;
    IsEmpty():boolean;
    Empty():void;
    GetAt(idx:number):number;
    SetAt(idx:number,ch:number):void;
    c_str():string;
    Compare(str:string):number;
    CompareNoCase(str:string):number;
    TrimBlank():void;
    InsertChar(idx:number,ch:number):number;
    Insert(idx:number,str:string):number;
    Delete(idx:number,count:number):number;
    ReplaceChar(oldChar:number,newChar:number):number;
    Replace(oldStr:string,newStr:string):number;
    Remove(ch:number):number;
    FindChar(ch:number,start:number):number;
    ReverseFind(ch:number):number;
    Find(str:string,start:number):number;
    GetBuffer(bufLen:number):string;
    ReleaseBuffer(bufLen:number):void;
    SetLength(len:number):void;
    Copy(src:IStringA):void;
    Assign(str:string):void;
    Assign2(str:string,len:number):void;
    ToUpper():void;
    ToLower():void;
    TrimRight(ch:number):void;
    TrimLeft(ch:number):void;
    Trim(ch:number):void;
    AppendChar(ch:number):void;
    AppendStr(str:string):void;
}


