import { IObjRef } from "soui4/IObjRef";
import { LPARAM, int } from "soui4/Typedef";

export class ITimer extends IObjRef{
	/**
     * StartTimer
     * @brief    启动定时器
     * @param    int nElapse --  延时(ms)
	 * @param    BOOL bRepeat -- 重复标志
	 * @param    LPARAM uData -- Timer 自定义参数
     * @return   BOOL, TRUE--成功
     * Describe
     */
	StartTimer(nElapse:int,bRepeat:boolean,uData:LPARAM) :boolean;

	/**
     * KillTimer
     * @brief    停止定时器
     * @return   void
     * Describe
     */
	KillTimer() :void;
}