import { IInterpolator } from "soui4/IInterpolator";
import { IObject } from "soui4/IObject";
import { COLORREF, ISwndContainer, int } from "soui4/Typedef";

export class IValueAnimator extends IObject{
    /**
     * set animator duration in ms
     * @param duration 
     */
    setDuration(duration:number):void;

    /**
     * get animator duration
     */
    getDuration():number;

    /**
     * get repeat count
     */
    getRepeatCount():number;

    /**
     * set repeat count
     * @param count 
     */
    setRepeatCount(count:number):void;

    /**
     * get repeat mode  0-restart, 1- reverse
     */
    getRepeatMode():number;

    /**
     * set repeat mode
     * @param mode repeat mode. 0-restart, 1- reverse
     */
    setRepeatMode(mode:number):void;

    /**
     * set interpolator
     * @param obj interpolator obj
     */
    setInterpolator(obj:IInterpolator):void;

    /**
     * get interpolator
     */
    getInterpolator():IInterpolator;

    /**
     * start 
     * @param obj timeline handdles manager. get by IWindow.GetContianer().
     */
    start(obj:ISwndContainer):void;

    /**
     * end the animator
     */
    end():void;

    /**
     * get whether the animator is running.
     */
    isRunning():boolean;

    /**
     * get whether the animator is started.
     */
    isStarted():boolean;

    /**
     * reverse the animator
     */
    reverse():void;

    /**
     * get fraction
     */
    getAnimatedFraction():number;

    /**
     * clone a new copy from this.
     */
    clone():IValueAnimator;

    /**
     * get start delay in ms
     */
    getStartDelay():number;

    /**
     * set start delay in ms
     * @param delay 
     */
    setStartDelay(delay:number):void;
}

/**
 * get int value from a IntValueAnimator
 * @param animator 
 */
export function GetIntAnimatorValue(animator:IValueAnimator):int;

/**
 * get float value from a FloatValueAnimator
 * @param animator 
 */
export function GetFloatAnimatorValue(animator:IValueAnimator):number;

/**
 * get Color value from a ColorValueAnimator
 * @param animator 
 */
export function GetColorAnimatorValue(animator:IValueAnimator):COLORREF;

