import { IObjRef } from "soui4/IObjRef";

export class IXmlAttr extends IObjRef{
    /**
     * is attr empty
     */
    IsEmpty():boolean;

    /**
     * attr name
     */
    Name():string;

    /**
     * attr value
     */
    Value():string;

    /**
     * set user data
     * @param uData 
     */
    set_userdata(uData:number):boolean;

    /**
     * get user data
     */
    get_userdata():number;

    /**
     * read as int
     */
    AsInt():number;

    /**
     * read as uint
     */
    AsUint():number;

    /**
     * read as float
     */
    AsFloat():number;

    /**
     * read as double
     */
    AsDouble():number;

    /**
     * read as boolean
     */
    AsBool():boolean;
}

export class IXmlNode extends IObjRef{
    /**
     * is node empty
     */
    IsEmpty():boolean;

    /**
     * render to string
     */
    ToString():string;

    /**
     * node name
     */
    Name():string;

    /**
     * node value(text)
     */
    Value():string;

    /**
     * set user data
     * @param uData 
     */
    set_userdata(uData:number):boolean;

    /**
     * get user data
     */
    get_userdata():number;

    /**
     * append a new child
     * @param name child name
     */
    AppendChild(name:string):IXmlNode;

    /**
     * prepend a new child
     * @param name child name
     */
    PrependChild(name:string):IXmlNode;

    /**
     * append a node from an exist node
     * @param node exist node
     */
    AppendCopyNode(node:IXmlNode):IXmlNode;

    /**
     * prepend a node from an exist node
     * @param node exist node
     */
    PrependCopyNode(node:IXmlNode):IXmlNode;

    /**
     * append a new attribute
     * @param name attribute name
     */
    AppendAttribute(name:string):IXmlAttr;

    /**
     * prepend a new attribute
     * @param name attribute name
     */
    PrependAttribute(name:string):IXmlAttr;

    /**
     * append a attribute from an exist attribute
     * @param attr exist attr object
     */
    AppendCopyAttribute(attr:IXmlAttr):IXmlAttr;

    /**
     * preppend a attribute from an exist attribute
     * @param attr exist attr object
     */
    PrependCopyAttribute(attr:IXmlAttr):IXmlAttr;
}


export class IXmlDoc extends IObjRef{
    /**
     * reset xml doc
     */
    Reset():void;

    /**
     * copy xml data from source xmldoc
     * @param src source xmldoc
     */
    Copy(src:IXmlDoc):void;

    /**
     * load xml from string
     * @param str xml string
     * @param opt load option
     */
    LoadString(str:string,opt:number):boolean;

    /**
     * load xml from file
     * @param path filename
     * @param opt load option
     * @param encoding xml encoding
     */
    LoadFile(path:string,opt:number,encoding:number):boolean;

    /**
     * save xml to file
     * @param path filname
     * @param indent indent string
     * @param flag 
     * @param encoding 
     */
    SaveFile(path:string,indent:string, flag:number,encoding:number):boolean;
}
