import { INativeWnd } from "soui4/INativeWnd";
import { HWND, LPARAM, UINT, WPARAM } from "soui4/Typedef";


export class MsgHandle{
    /**
     * msg handled flag
     */
    msgHandled:boolean;
}

export class MsgHandlerProxy{
    constructor();
    
    /**
     * install hook to msg handler
     * @param pNativeWnd native hwnd
     * @param thisObj callback this object
     * @param fun callback function
     */
    InstallHook(pNativeWnd:INativeWnd, thisObj:object, fun:(hwnd:HWND,uMsg:UINT,wp:WPARAM,lp:LPARAM,msgHande:MsgHandle)=>number):boolean;

    /**
     * remove msg handler hook
     * @param pNativeWnd native wnd object
     */
	UninstallHook(pNativeWnd:INativeWnd):void;
}
