import { IStringA } from "soui4/IString";

export class SDropTarget{
    constructor();
    
    /**
     * get drop filename
     * @param iFile file index
     * @param out filename
     */
    GetDropFileName(iFile:number, out:IStringA):boolean;

    /**
     * call back function
     */
    onDrop:(files:number)=>void;

    /**
     * call back this object
     */
    cbHandler:object;
}