import { HWND } from "soui4/Typedef";

export class SFileOpenDialog{
    constructor();
    
    /**
     * is save dialog
     */
    bSave:boolean;

    /**
     * default file extention
     */
    defExt:string;

    /**
     * default file name
     */
    defname:string;

    /**
     * init folder name
     */
    defaultFolder:string;

    /**
     * flags
     */
    flag:number;

    /**
     * add file filter
     * @param name 
     * @param pattern 
     */
    AddFilter(name:string,pattern:string):void;

    /**
     * get the return file path
     */
    GetFilePath():string;

    /**
     * get the return file title
     */
    GetFileTitle():string;

    /**
     * do modal
     */
    DoModal():number;

    /**
     * do modal use an owner
     * @param owner 
     */
    DoModal2(owner:HWND):number;
}