import { IEvtArgs } from "soui4/IEvt";
import { IHostDialog, IHostWnd } from "soui4/IHostWnd";
import { MsgHandle } from "soui4/MsgHandlerProxy";
import { HWND } from "soui4/Typedef";

export class JsHostWnd extends IHostWnd{
    /**
     * constructor
     * @param resId layout resource id
     */
    constructor(resId:string);

    /**
     * callback this object
     */
    cbHandler:object;

    /**
     * event callback function
     */
    onEvt:(evt:IEvtArgs)=>boolean;

    /**
     * msg callback function
     */
    onMsg:(hWnd:HWND,uMsg:number,wp:number,lp:number,msgHandle:MsgHandle)=>number;
}

export class JsHostDialog extends IHostDialog{
    /**
     * layout resource id
     * @param resId resource id
     */
    constructor(resId:string);

    /**
     * callback this object
     */
    cbHandler:object;

    /**
     * event callback function
     */    
    onEvt:(evt:IEvtArgs)=>boolean;

    /**
     * msg callback function
     */
    onMsg:(hWnd:HWND,uMsg:number,wp:number,lp:number,msgHandle:MsgHandle)=>number;
}
