import { IEvtArgs } from "soui4/IEvt";
import { IHostDialog, IHostWnd } from "soui4/IHostWnd";
import { MsgHandle } from "soui4/MsgHandlerProxy";
import { HWND, LPVOID } from "soui4/Typedef";

export class JsHostWnd extends IHostWnd{
    /**
     * constructor
     * @param resId layout resource id
     */
    constructor(resId:string);

    /**
     * set required event id and control id
     * @param evtId event id
     * @param ctrlId control id
     * @param bSet true-set, false-remove
     */
    RequireEventForCtrlID(evtId:number, ctrlId:number, bSet:boolean):void;

    /**
     * set required event id and control name
     * @param evtId event id
     * @param ctrlName control name
     * @param bSet true-set, false-remove
     */
    RequireEventForCtrlName(evtName:string, ctrlName:string, bSet:boolean):void;

    /**
     * set required message id
     * @param msgId message id
     * @param bSet true-set, false-remove
     */
    RequireMessage(msgId:number, bSet:boolean):void;

    /**
     * set presenter
     * @param presenter 
     */
    SetPresenter(presenter:LPVOID):void;

    /**
     * get scale
     */
    GetScale():number;

    /**
     * callback this object
     */
    cbHandler:object;

    /**
     * event callback function
     */
    onEvt:(evt:IEvtArgs)=>boolean;

    /**
     * msg callback function
     */
    onMsg:(hWnd:HWND,uMsg:number,wp:number,lp:number,msgHandle:MsgHandle)=>number;
}

export class JsHostDialog extends IHostDialog{
    /**
     * layout resource id
     * @param resId resource id
     */
    constructor(resId:string);

    /**
     * set presenter
     * @param presenter 
     */
    SetPresenter(presenter:LPVOID):void;

    /**
     * get scale
     */
    GetScale():number;

    /**
     * callback this object
     */
    cbHandler:object;

    /**
     * event callback function
     */    
    onEvt:(evt:IEvtArgs)=>boolean;

    /**
     * msg callback function
     */
    onMsg:(hWnd:HWND,uMsg:number,wp:number,lp:number,msgHandle:MsgHandle)=>number;
}
