import { IMatrix } from "soui4/IMatrix";

export class SMatrix extends IMatrix{
    getAt(idx:int):float;
    setAt(idx:int,val:float):void;
    invert(mtx:SMatrix):boolean;
    setMatrix(data:ArrayBuffer):void;
    rotate(degrees:float):SMatrix;
    translate(dx:float,dy:float):SMatrix;
    scale(sx:float,sy:flaot):SMatrix;
    shear(sh:float,sv:float):SMatrix;
}