import { DWORD, int } from "soui4/Typedef";

export class SRegKey {
    /**
     * open a register key
     * @param parent reg root. @link SKEY_ROOT SKEY_USER SKEY_MACHINE SKEY_USERS
     * @param pszSubKey subkey path
     * @param mode open mode. @link REG_READ REG_WRITE
     */
    Open(parent:int,pszSubKey:string,mode:int):boolean;

    /**
     * close
     */
    Close():void;

    /**
     * get string value
     * @param key key
     */
    GetStringValue(key:string):string;

    /**
     * set string value
     * @param key key
     * @param value value
     */
    SetStringValue(key:string,value:string):boolean;

    /**
     * get dword value
     * @param key 
     */
    GetDwordValue(key:string):DWORD;

    /**
     * set dword value
     * @param key 
     * @param value 
     */
    SetDwordValue(key:string,value:DWORD):boolean;
}
