import { IXmlAttr, IXmlNode ,IXmlDoc} from "soui4/IXml";

export class SXmlAttr extends IXmlAttr{
    /**
     * next attribute
     */
    Next():SXmlAttr;

    /**
     * prev attribute
     */
    Prev():SXmlAttr;
}

export class SXmlNode extends IXmlNode{
    /**
     * get attribute of name
     * @param name attribute name
     * @param caseSensitive case sensitive flag
     */
    Attribute(name:string,caseSensitive:boolean):SXmlAttr;

    /**
     * get the first attribute
     */
    FirstAttribute():SXmlAttr;

    /**
     * get the last attribute
     */
    LastAttribute():SXmlAttr;

    /**
     * get a child node of name
     * @param name child node name
     * @param caseSensitive case sensitive flag
     */
    Child(name:string,caseSensitive:boolean):SXmlNode;

    /**
     * get first child node
     */
    FirstChild():SXmlNode;

    /**
     * get last child node
     */
    LastChild():SXmlNode;

    /**
     * get next sibling node
     */
    NextSibling():SXmlNode;

    /**
     * get next sibling node of name
     * @param name node name
     * @param caseSensitive case sensitive flag
     */
    NextSibling2(name:string,caseSensitive:boolean):SXmlNode;

    /**
     * get prev sibling node
     */
    PrevSibling():SXmlNode;

    /**
     * get prev sibling node of name
     * @param name node name
     * @param caseSensitive case sensitive flag
     */
    PrevSibling2(name:string,caseSensitive:boolean):SXmlNode;
}

export class SXmlDoc extends IXmlDoc{
    /**
     * get root node of xml doc.
     */
    Root():SXmlNode;
}

